#!/bin/env python

from typing import List
from math import sqrt

MOD = 998244353


def sieve(n) -> List[bool]:
    is_prime = [True] * (n + 1)
    is_prime[0] = is_prime[1] = False
    for i in range(4, n + 1, 2):
        is_prime[i] = False

    for i in range(3, int(sqrt(n)) + 2, 2):
        if is_prime[i]:
            for j in range(i * i, n, 2 * i):
                is_prime[j] = False

    return is_prime


def main():
    n, m = map(int, input().split())
    is_prime = sieve(n)

    unambiguous = 0
    last = 1
    primes_acc = 1

    for i in range(1, n + 1):
        if primes_acc > m:
            break

        if is_prime[i]:
            primes_acc *= i

        last *= m // primes_acc
        unambiguous = (unambiguous + last) % MOD


    if m != 1:
        all = pow(m, n + 1, MOD) - 1
        all = (all * pow(m - 1, MOD - 2, MOD) - 1 + MOD) % MOD
    else:
        all = n % MOD

    ans = (all - unambiguous + MOD) % MOD
    print(ans)


if __name__ == "__main__":
    main()

