/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskD solver = new TaskD();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskD {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            long n = in.nextLong();
            long m = in.nextLong();
            ModuloArithmetic moduloArithmetic = new ModuloArithmetic(998244353L);

            long factm = moduloArithmetic.factorial(m);
            long fact1 = moduloArithmetic.factorial(m - (n - 1));
            long fact2 = moduloArithmetic.factorial(n - 1);
            long pow2 = n >= 3 ? moduloArithmetic.powmod(2, n - 3) : 1;
            long t = moduloArithmetic.mul(fact1, fact2);
            long ans = moduloArithmetic.div(factm, t);
            ans = moduloArithmetic.mul(ans, n - 2);
            ans = moduloArithmetic.mul(ans, pow2);
            out.println(ans);
        }

    }

    static class InputReader {
        final InputStream is;
        final byte[] buffer = new byte[1024];
        int curCharIdx;
        int nChars;

        public InputReader(InputStream is) {
            this.is = is;
        }

        public int read() {
            if (curCharIdx >= nChars) {
                try {
                    curCharIdx = 0;
                    nChars = is.read(buffer);
                    if (nChars == -1)
                        return -1;
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return buffer[curCharIdx++];
        }

        public long nextLong() {
            int sign = 1;
            int c = skipDelims();
            if (c == '-') {
                sign = -1;
                c = read();
                if (isDelim(c))
                    throw new RuntimeException("Incorrect format");
            }
            long val = 0;
            while (c != -1 && !isDelim(c)) {
                if (!isDigit(c))
                    throw new RuntimeException("Incorrect format");
                val = 10L * val + (c - '0');
                c = read();
            }
            return val * sign;
        }

        private final int skipDelims() {
            int c = read();
            while (isDelim(c)) {
                c = read();
            }
            return c;
        }

        private static boolean isDelim(final int c) {
            return c == ' ' ||
                    c == '\n' ||
                    c == '\t' ||
                    c == '\r' ||
                    c == '\f';
        }

        private static boolean isDigit(final int c) {
            return '0' <= c && c <= '9';
        }

    }

    static class Algorithms {
        public static long powmod(long a, long n, long mod) {
            long res = 1;
            while (n != 0) {
                if ((n & 1) == 1)
                    res = (res * a) % mod;
                a = (a * a) % mod;
                n >>= 1;
            }
            return res;
        }

        public static long inverseByModulo(long a, long n) {
            long t = 0, newt = 1, r = n, newr = a;
            while (newr != 0) {
                long q = r / newr;

                long temp = newt;
                newt = t - q * newt;
                t = temp;

                temp = newr;
                newr = r - q * newr;
                r = temp;
            }

            if (r > 1)
                return 0;
            if (t < 0)
                t += n;
            return t;
        }

    }

    static class ModuloArithmetic {
        final long MODULO;

        public ModuloArithmetic(long mod) {
            this.MODULO = mod;
        }

        public long mul(long a, long b) {
            return (a * b) % MODULO;
        }

        public long div(long a, long b) {
            return (a * Algorithms.inverseByModulo(b, MODULO)) % MODULO;
        }

        public long factorial(long n) {
            long ans = 1;
            for (int i = 2; i <= n; i++) ans = mul(ans, i);
            return ans;
        }

        public long powmod(long k, long n) {
            return Algorithms.powmod(k, n, MODULO);
        }

    }
}

