	void run()throws IOException
	{
		br = new BufferedReader(new InputStreamReader(System.in));
		bw=new BufferedWriter(new OutputStreamWriter(System.out));
		solve();
	}
	void solve() throws IOException
	{
		int limit=100001;
		int fac[]=new int[limit];
		int invfac[]=new int[limit];
		fac[0]=1;
		invfac[0]=1;
		int mod=1000000007;
		for(int i=0;++i<limit;)
		{
			long x=(long)fac[i-1]*i;
			x%=mod;
			fac[i]=(int)x;
			invfac[i]=(int)nPowerM(fac[i], mod-2);
		}

		int t=1;
		while(t-->0)
		{
			int n=ni();
			int k=ni();

			long ans=1;
			k=Math.min(k, n);
			for(int i=0;++i<=k;)
			{
				long x=fac[n];
				x*=invfac[i];
				x%=mod;
				x*=invfac[n-i];
				x%=mod;
				
				ans+=x;
				ans%=mod;
			}
			
			bw.write(ans+"\n");

		}
		bw.flush();
	}

	public long mod(long a)
	{
		int mod=1000000007;
		a%=mod;
		if(a<0)
			a+=mod;
		
		return a;
	}

	public long nPowerM(int n, int m)
	{
		if(m==0)
			return 1L;
		
		int mod=1000000007;
		long ans=nPowerM(n, m/2);
		ans*=ans;
		ans%=mod;
		
		if(m%2==1)
		{
			ans*=n;
			ans%=mod;
		}
		
		return ans;
	}
