        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        FastScanner in = new FastScanner(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskC solver = new TaskC();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskC {
        public static boolean angle(TaskC.Point a, TaskC.Point b) {
            double amag = a.mag();
            double bmag = b.mag();
            double abmag = a.v * b.v + a.w * b.w + a.x * b.x + a.y * b.y + a.z * b.z;
            double angle = Math.toDegrees(Math.acos(abmag / (amag * bmag)));
            if (angle < 90) {
                return true;
            }
            return false;
        }

        public void solve(int testNumber, FastScanner in, PrintWriter out) {
            int n = in.nextInt();
            if (n > 11) {
                out.println("0");
            } else {
                HashSet<Integer> good = new HashSet<Integer>();
                ArrayList<TaskC.Point> arr = new ArrayList<>();
                for (int i = 0; i < n; i++) {
                    int a = in.nextInt();
                    int b = in.nextInt();
                    int c = in.nextInt();
                    int d = in.nextInt();
                    int e = in.nextInt();
                    TaskC.Point p = new TaskC.Point(a, b, c, d, e);
                    arr.add(p);
                }
                for (int i = 0; i < n; i++) {
                    boolean b = true;
                    for (int j = 0; j < n; j++) {
                        for (int k = 0; k < n; k++) {
                            if (i != j && i != k && j != k) {
                                TaskC.Point one = arr.get(j);
                                TaskC.Point two = arr.get(k);
                                TaskC.Point three = arr.get(i);
                                TaskC.Point lol2 = new TaskC.Point(one.v - three.v, one.w - three.w, one.x - three.x, one.y - three.y, one.z - three.z);
                                TaskC.Point lol1 = new TaskC.Point(two.v - three.v, two.w - three.w, two.x - three.x, two.y - three.y, two.z - three.z);
                                if (angle(lol1, lol2)) {
                                    b = false;
                                }
                            }
                        }
                    }
                    if (b) {
                        good.add(i + 1);
                    }
                }
                if (good.size() == 0) {
                    out.println("0");
                } else {
                    out.println(good.size());
                    for (int i : good) {
                        out.println(i);
                    }
                }
            }
        }

        public static class Point {
            int v;
            int w;
            int x;
            int y;
            int z;

            public Point(int a, int b, int c, int d, int e) {
                v = a;
                w = b;
                x = c;
                y = d;
                z = e;
            }

            public double mag() {
                return Math.sqrt(v * v + w * w + x * x + y * y + z * z);
            }

        }

    }

    static class FastScanner {
        public BufferedReader reader;
        public StringTokenizer tokenizer;

        public FastScanner(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine());
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

