  static class TaskB {
    char[][] board;

    public void solve(int testNumber, QuickScanner in, QuickWriter out) {
      board = new char[9][9];
      for (int i = 0; i < 9; ++i)
        for (int j = 0; j < 9; ++j) {
          board[i][j] = (char) in.nextNonSpaceChar();
        }
      int x = (in.nextInt() - 1) % 3 * 3;
      int y = (in.nextInt() - 1) % 3 * 3;
      int count = 0;
