          for (int j = y; j < y + 3; ++j)
            if (board[i][j] == '.') {
              board[i][j] = '!';
            }
      } else {
        for (int i = 0; i < 9; ++i)
          for (int j = 0; j < 9; ++j)
            if (board[i][j] == '.') {
              board[i][j] = '!';
            }
      }
      for (int i = 0; i < 9; ++i) {
