public class Main {
    static int []arr;
    static int [][]dp;
    static String s;
    static ArrayList<Integer>[]G;

    public static int solve(int idx,int rem)
    {
        if (rem<=0) return 0;
        if (dp[idx][rem]!=-1) return dp[idx][rem];
        dp[idx][rem]=(int)1e6;
        for (int i=1;i<=3;++i)
        {
            if (i==1&&s.charAt(idx-1)=='R') continue;
            if (i==2&&s.charAt(idx-1)=='G') continue;
            if (i==3&&s.charAt(idx-1)=='B') continue;
            for (int u:G[i])
                if (arr[u]>arr[idx])
                    dp[idx][rem]=Math.min(dp[idx][rem],(solve(u, rem - arr[u])+Math.abs(u-idx)));
        }
        return dp[idx][rem];
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter out = new PrintWriter(System.out);

        int n=sc.nextInt();
        int pos=sc.nextInt();
        int k=sc.nextInt();
        G = new ArrayList[5];
        for (int i=0;i<5;++i) G[i] = new ArrayList();
        arr =new int [n+5];
        dp=new int [n+5][k+5];
        for (int i=0;i<=n+1;++i)
            for (int j=0;j<=k+1;++j)
                dp[i][j]=-1;

        for (int i=1;i<=n;++i) arr[i]=sc.nextInt();
        s=sc.nextLine();
        for (int i=0;i<n;++i)
        {
            char ch=s.charAt(i);
            if ( ch == 'R')
                G[1].add(i + 1);
            else if (ch == 'G')
                G[2].add(i + 1);
            else G[3].add(i + 1);
        }

        int cnt=(int)1e6;
        for (int i=1;i<=n;++i)
            cnt=Math.min(cnt,solve(i,k-arr[i])+Math.abs(i-pos));

        out.println((cnt>=(int)1e6)?-1:cnt);
        out.close();
    }
    static class Scanner
    {
        StringTokenizer st;
        BufferedReader br;

        public Scanner(InputStream s){	br = new BufferedReader(new InputStreamReader(s));}

        public String next() throws IOException
        {
            while (st == null || !st.hasMoreTokens())
                st = new StringTokenizer(br.readLine());
            return st.nextToken();
        }

        public int nextInt() throws IOException {return Integer.parseInt(next());}

        public long nextLong() throws IOException {return Long.parseLong(next());}

        public String nextLine() throws IOException {return br.readLine();}
    }
}

