	double prob(int l, int r, int a) {
		return Math.max(r - Math.max(l, a) + 1, 0) / (r - l + 1.0);
	}
	void main() {
		int n = sc.nextInt();
		int[] ll = new int[n];
		int[] rr = new int[n];
		for (int i = 0; i < n; i++) {
			ll[i] = sc.nextInt();
			rr[i] = sc.nextInt();
		}
		double ans = 0;
		for (int a = 1; a <= 10000; a++) {
			double p = 1;
			for (int j = 0; j < n; j++)
				p *= 1 - prob(ll[j], rr[j], a);
			double q = p;
			for (int i = 0; i < n; i++) {
				p = prob(ll[i], rr[i], a);
				for (int j = 0; j < n; j++)
					if (j != i)
						p *= 1 - prob(ll[j], rr[j], a);
				q += p;
			}
			ans += 1 - q;
		}
		printf("%.9f\n", ans);
	}
}

