public class D908
{
	int k, mod = (int) (1e9 + 7);
	long pa, pb, pa_pb;
	long dp[][];

	void solve()
	{
		k = ni();
		dp = new long[k + 5][k + 5];
		for (int i = 0; i < k + 5; i++)
		{
			Arrays.fill(dp[i], -1);
		}
		int a = ni();
		int b = ni();
		pa = mul(a, pow(a + b, mod - 2, mod));
		pb = mul(b, pow(a + b, mod - 2, mod));
		pa_pb = mul(a, pow(b, mod - 2, mod));
		out.println(rec(1, 0));
	}

	long rec(int a, int ab)
	{
		if (ab >= k)
			return ab;
		if (a >= k)
		{
			return (a + 0L + ab + pa_pb) % mod;
		}
		if (dp[a][ab] != -1)
			return dp[a][ab];
		dp[a][ab] = rec(a + 1, ab) * pa + 0L + rec(a, ab + a) * pb;
		dp[a][ab] %= mod;
		return (dp[a][ab]);
	}

	long add(long a, long b)
	{
		long sum = a + b;
		if (sum >= mod)
			sum -= mod;
		return sum;
	}

	long sub(long a, long b)
	{
		long sub = a - b;
		if (sub < 0)
			sub += mod;
		return sub;
	}

	long mul(long a, long b)
	{
		return (a * b) % mod;
	}

	long pow(long x, long n, long mod)
	{
		long res = 1;
		x %= mod;
		while (n > 0)
		{
			if (n % 2 == 1)
			{
				res = (res * x) % mod;
			}
			x = (x * x) % mod;
			n /= 2;
		}
		return res;
	}

	void run() throws Exception
	{
		out = new PrintWriter(System.out);
		reader = new BufferedReader(new InputStreamReader(System.in), 32768);
		tokenizer = null;
		solve();
		out.flush();
	}

	public int ni()
	{
		return Integer.parseInt(next());
	}

	BufferedReader reader;
	StringTokenizer tokenizer;
	PrintWriter out;

	public static void main(String[] args) throws Exception
	{
		new D908().run();
	}

	public String next()
	{
		while (tokenizer == null || !tokenizer.hasMoreTokens())
		{
			try
			{
				tokenizer = new StringTokenizer(reader.readLine());
			} catch (IOException e)
			{
				throw new RuntimeException(e);
			}
		}
		return tokenizer.nextToken();
	}
}

