import java.io.*;
import java.util.InputMismatchException;


public class CF959E {

    public static void main(String[] args) throws IOException {
        InputReader in = new InputReader(System.in);
        OutputWriter out = new OutputWriter(System.out);
        final long N = in.readLong() - 1;
        long res = 0;
        long i = 1;
        for (i = 1; i <= N; i = i << 1) {
            res = res + ((N - i) / (i << 1) + 1) * i;
        }
        out.print(res);
        closeStreams(out, in);
    }

    private static void closeStreams(OutputWriter out, InputReader in) throws IOException {
        out.flush();
        out.close();
        in.close();
    }

    static class InputReader {

        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int read() {
            if (numChars == -1)
                throw new InputMismatchException();
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0)
                    return -1;
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9')
                    throw new InputMismatchException();
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public double readDouble() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            double res = 0;
            while (!isSpaceChar(c) && c != '.') {
                if (c == 'e' || c == 'E') {
                    return res * Math.pow(10, readInt());
                }
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            }
            if (c == '.') {
                c = read();
                double m = 1;
                while (!isSpaceChar(c)) {
                    if (c == 'e' || c == 'E') {
                        return res * Math.pow(10, readInt());
                    }
                    if (c < '0' || c > '9') {
                        throw new InputMismatchException();
                    }
                    m /= 10;
                    res += (c - '0') * m;
                    c = read();
                }
            }
            return res * sgn;
        }

        public long readLong() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            long res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public String readString() {
            int c = read();
            while (isSpaceChar(c))
                c = read();
            StringBuilder res = new StringBuilder();
            do {
                res.appendCodePoint(c);
                c = read();
            } while (!isSpaceChar(c));
            return res.toString();
        }

        public boolean isSpaceChar(int c) {
            if (filter != null) {
                return filter.isSpaceChar(c);
            }
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public boolean isEndOfLine(int c) {
            if (filter != null) {
                return filter.isEndOfLine(c);
            }
            return c == '\n' || c == '\r' || c == -1;
        }

        public String next() {
            return readString();
        }

        public void close() throws IOException {
            this.stream.close();
        }

        public interface SpaceCharFilter {
            boolean isSpaceChar(int ch);

            boolean isEndOfLine(int ch);
        }

    }

    static class IOUtils {

        public static int[] readIntArray(InputReader in, int elementCount) {
            return readIntArray(in, elementCount, 0);
        }

        public static int[] readIntArray(InputReader in, int elementCount, int startOffset) {
            int[] array = new int[elementCount + startOffset];
            for (int i = 0; i < elementCount; i++)
                array[i + startOffset] = in.readInt();
            return array;
        }

        public static long[] readLongArray(InputReader in, int elementCount) {
            return readLongArray(in, elementCount, 0);
        }

        public static long[] readLongArray(InputReader in, int elementCount, int startOffset) {
            long[] array = new long[elementCount + startOffset];
            for (int i = 0; i < elementCount; i++)
                array[i + startOffset] = in.readLong();
            return array;
        }

    }

    static class OutputWriter {

        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void print(Object... objects) {
            for (int i = 0; i < objects.length; i++) {
                if (i != 0)
                    writer.print(' ');
                writer.print(objects[i]);
            }
        }

        public void printLine(Object... objects) {
            print(objects);
            writer.println();
        }

        public void close() {
            writer.close();
        }

        public void flush() {
            writer.flush();
        }

    }
}

// Let f(x) = # of sequences a_i such that gcd() = 1, sum(a_i)=x. We want f(y - x). This is a hard AF function to calculate: easiest recurrence would be sum(f_i) where i<t, gcd(t,i)=1,
// but that's not fast enough at all.

// Trick from editorial: Relate a function to an easier function with a relationship IN EITHER DIRECTION. Here, let g(t) = # of sequences w/o gcd()=1 condition.
// Then, we realize that a_i can onyl have so many gcd's, so g(t)=sum_(d|t) f(t/d), f(t)=g(t)-sum_(d|t, d not t) f(t).

// Notice the repeats in f(t). That means we can DP it. Let's code!

import java.util.*;
public class Unusual {
	private static final int N = 1000000007;
	public static long pow(int n) {
		if (n == 0) return 1;
		long x = pow(n / 2);
		x = (x * x) % N;
		if (n % 2 == 1) x = (x * 2) % N;
		return x;
	}

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int x = in.nextInt(), y = in.nextInt(), t = y / x, i;
		if (y % x != 0) {
			System.out.println(0);
			System.exit(0);
		}
		ArrayList<Integer> divisors = new ArrayList<Integer>();
		for (i = 1; i * i < t; i++) {
			if (t % i == 0) {
				divisors.add(i); divisors.add(t / i);
			}
		}
		if (i * i == t) divisors.add(i);
		int[] d = new int[divisors.size()];
		for (i = 0; i < d.length; i++) {
			d[i] = divisors.get(i);
		}
		Arrays.sort(d);
		long[] f = new long[divisors.size()];
		f[0] = 1;
		for (i = 1; i < d.length; i++) {
			f[i] = pow(d[i] - 1);
			for (int j = 0; j < i; j++) {
				if (d[i] % d[j] == 0) f[i] = (f[i] - f[j] + N) % N;
			}
		}
//		System.out.println(Arrays.toString(d));
//		System.out.println(Arrays.toString(f));
		System.out.println(f[d.length - 1]);
	}
}

