public class C_Round_245_Div1 {

    public static long MOD = 1000000007;
    static int[] dp;

    public static void main(String[] args) throws FileNotFoundException {
        // PrintWriter out = new PrintWriter(new FileOutputStream(new File(
        // "output.txt")));
        PrintWriter out = new PrintWriter(System.out);
        Scanner in = new Scanner();
        int n = in.nextInt();
        int[] data = new int[n];
        for (int i = 0; i < n; i++) {
            data[i] = in.nextInt();
        }
        Arrays.sort(data);

        if (data[n - 1] == n && data[0] == 1) {
            dp = new int[1 << n];
            boolean v = cal(0, 0, data);
            if (v) {
                out.println("YES");
            } else {
                out.println("NO");
            }
        } else {
            out.println("NO");
        }
        out.close();
    }

    static boolean cal(int index, int mask, int[] data) {
        if (index == data.length) {
            return Integer.bitCount(mask) == 1;
        }
        // System.out.println(index + " " + Integer.toBinaryString(mask));
        int num = (index + 1) * mask;
        int x = num / 30;
        int y = num % 30;
        if ((dp[x] & (1 << y)) != 0) {
            return false;
        }
        dp[x] |= (1 << y);
        if (data[index] == 1) {
            return cal(index + 1, mask | (1 << index), data);
        } else {

            for (int i = mask;; i = (i - 1) & mask) {

                if (Integer.bitCount(i) > 1) {
                    int sum = 0;
                    for (int j = 0; j < data.length; j++) {
                        if (((1 << j) & i) != 0) {
                            sum += data[j];
                        }
                    }
                    // System.out.println(sum + " " + data[index] + " " + Integer.bitCount(mask));
                    if (sum + 1 == data[index]) {
                        if (cal(index + 1, (mask ^ i) | (1 << index), data)) {
                            return true;
                        }
                    }
                }

                if (i == 0) {
                    break;
                }
            }
            return false;
        }

    }

    public static int[] KMP(String val) {
        int i = 0;
        int j = -1;
        int[] result = new int[val.length() + 1];
        result[0] = -1;
        while (i < val.length()) {
            while (j >= 0 && val.charAt(j) != val.charAt(i)) {
                j = result[j];
            }
            j++;
            i++;
            result[i] = j;
        }
        return result;

    }

    public static boolean nextPer(int[] data) {
        int i = data.length - 1;
        while (i > 0 && data[i] < data[i - 1]) {
            i--;
        }
        if (i == 0) {
            return false;
        }
        int j = data.length - 1;
        while (data[j] < data[i - 1]) {
            j--;
        }
        int temp = data[i - 1];
        data[i - 1] = data[j];
        data[j] = temp;
        Arrays.sort(data, i, data.length);
