public class Run {

	public static void main(String[] args)
	{
		Scanner input = new Scanner(System.in);
		String wholeInput = input.nextLine();
		String[] token = wholeInput.split(" ");
		int n = Integer.valueOf(token[0]);
		int m = Integer.valueOf(token[1]);
		wholeInput = input.nextLine();
		token = wholeInput.split(" ");
		int[] a = new int[n];
		for (int i = 0; i < n; i++)
		{
			a[i] = Integer.valueOf(token[i]);
		}
		input.close();
		token = null;
		int S = 0;
		for (int i = 0; i < n; i++)
		{
			S += a[i];
		}
		if (m > S)
		{
			System.out.print(-1);
		}
		else
		{
			System.out.print(solve(1, m, m, a));
		}
	}

	private static boolean checkAnswer(int m, int day, int[] a)
	{
		for (int i = 0; i < a.length; i++)
		{
			a[i] = -a[i];
		}
		Arrays.sort(a);
		for (int i = 0; i < a.length; i++)
		{
			a[i] = -a[i];
		}
		int arrayMarker = 0;
		int cupMarker = 0;
		int S = 0;
		while(true)
		{
			if (a[arrayMarker] - cupMarker > 0)
			{
				S += a[arrayMarker] - cupMarker;
			}
			arrayMarker++;
			if (arrayMarker % day == 0)
			{
				cupMarker++;
			}
			if (arrayMarker == a.length)
			{
				break;
			}
		}
		if (S >= m)
		{
			return true;
		}
		else
		{
			return false;
		}
	}

	private static int solve(int tempPos, int length, int m, int[] a)
	{
		int checkPos = tempPos + length / 2;
		if (length == 0)
		{
			return tempPos;
		}
		if (checkAnswer(m, checkPos, a))
		{
			return solve(tempPos, checkPos - tempPos, m, a);
		}
		else
		{
			return solve(checkPos + 1, tempPos + length - checkPos - 1, m, a);
		}
	}

}

