public class Main {

  public static void main(String[] args) {
    InputStream inputStream = System.in;
    OutputStream outputStream = System.out;
    FastIO in = new FastIO(inputStream);
    PrintWriter out = new PrintWriter(outputStream);
    TaskA1 solver = new TaskA1();
    solver.solve(1, in, out);
    out.close();
  }

  static class TaskA1 {

    public void solve(int testNumber, FastIO in, PrintWriter out) {
      int n = in.nextInt();
      int k = in.nextInt();
      int[] query = in.nextIntArray(n);
      int cost = 0;
      Set<Integer> availableBooks = new HashSet<>(k);
      int[] access = new int[81];
      int removable = -1;
      for (int i = 0; i < n; i++) {
        access[query[i]]++;
      }
      for (int i = 0; i < n; i++) {
        int currentBook = query[i];
        boolean doesNotContain = !availableBooks.contains(currentBook);
        if (doesNotContain) {
          cost++;
        }
        if (removable != -1) {
          availableBooks.remove(removable);
          removable = -1;
        } else if (availableBooks.size() == k && doesNotContain) {
          int removableBook = -1;
          int count = k;
          Set<Integer> known = new HashSet<>();
          for (int j = i + 1; j < n && count > 0; j++) {
            if (availableBooks.contains(query[j]) && !known.contains(query[j])) {
              count--;
              known.add(query[j]);
              removableBook = query[j];
            }
          }
          if (removableBook != -1) {
            availableBooks.remove(removableBook);
          } else {
            availableBooks.remove(availableBooks.iterator().next());
          }
        }
        availableBooks.add(currentBook);
        if (--access[currentBook] == 0) {
          removable = currentBook;
        }
      }
      out.println(cost);
    }

  }

  static class FastIO {

    final private int BUFFER_SIZE = 1 << 16;
    private InputStream din;
    private byte[] buffer;
    private int bufferPointer;
    private int bytesRead;

    public FastIO() {
      buffer = new byte[BUFFER_SIZE];
      bufferPointer = bytesRead = 0;
    }

    public FastIO(InputStream inputStream) {
      this();
      din = new DataInputStream(inputStream);
    }

    public FastIO(String file_name) {
      try {
        din = new DataInputStream(new FileInputStream(file_name));
        buffer = new byte[BUFFER_SIZE];
        bufferPointer = bytesRead = 0;
      } catch (FileNotFoundException e) {
        e.printStackTrace();
      }
    }

    public int nextInt() {
      int ret = 0;
      try {
        byte c = read();
        while (c <= ' ') {
          c = read();
        }
        boolean neg = (c == '-');
        if (neg) {
          c = read();
        }
        do {
          ret = ret * 10 + c - '0';
        } while ((c = read()) >= '0' && c <= '9');

        if (neg) {
          return -ret;
        }
      } catch (Exception e) {
        e.printStackTrace();
      }
      return ret;
    }

    public int[] nextIntArray(int n) {
      int[] arr = new int[n];
      for (int i = 0; i < n; i++) {
        arr[i] = nextInt();
      }
      return arr;
    }

    private void fillBuffer() {
      try {
        bytesRead = din.read(buffer, bufferPointer = 0, BUFFER_SIZE);
        if (bytesRead == -1) {
          buffer[0] = -1;
        }
      } catch (IOException e) {
        e.printStackTrace();
      }
    }

    private byte read() {
      if (bufferPointer == bytesRead) {
        fillBuffer();
      }
      return buffer[bufferPointer++];
    }

  }
}

