    ////////////////////////////////////////////////////////////////

    final int mod = (int)1e9 + 7;
    int n, k;
    int[][] dp0, dp1;
    int[] pow2 = new int[600];
    {
        pow2[0] = 1;
        for (int i = 1; i < pow2.length; i++) {
            pow2[i] = (pow2[i-1] * 2) % mod;
        }
    }

    void solve(){
        n = sc.nextInt();
        k = sc.nextInt();
        dp0 = new int[k+1][n+1];
        dp1  = new int[k+1][n+1];
        for (int[] val : dp0) fill(val, 0);

        for (int sz = 1; sz <= n; sz++) {

            for (int cnt  = 0; cnt <= k; cnt++) {
                int[] dp1_cnt = dp1[cnt];
                int[] dp1_cnt1 = null; if( cnt+1<=k ) dp1_cnt1 = dp1[cnt+1];
                int[] dp0_cnt = dp0[cnt];
                for (int last = 0; last <= sz; last++) {
                    if( cnt == 0 ){
                        if( last == 0 ) dp1_cnt[last] = 1;
                        else dp1_cnt[last] = 0;
                    }
                    else if( sz == last ){
                        if( cnt == 1 ){
                            dp1_cnt[last] = pow2[last] - 1;
                        }
                    }
                    else{
                        if( dp0_cnt[last] !=0  )
                            dp1_cnt[last] = (int)( (1L * dp0_cnt[last] * pow2[last]) % mod );
                    }

                    if( 1<=last && last <= sz-1   &&   1 <= cnt && cnt+1 <= k ){
                        if( dp1_cnt[last] != 0 )
                        dp1_cnt1[sz] =
                                (int)(
                                    (dp1_cnt1[sz] + 1L*dp1_cnt[last] * (pow2[sz-last]-1)) % mod
                                );
                    }
                }
            }
           //
            for (int cnt  = 0; cnt <= k; cnt++) {
                System.arraycopy( dp1[cnt],0, dp0[cnt],0, dp0[cnt].length );
                fill( dp1[cnt], 0 );
            }
        }
        long ans = 0;
        for (int last = 0; last <= n; last++) {
            ans += dp0[k][last];
        }
        ans %= mod;
        out.println( ans );
    }


}

