public class A17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int c = sc.nextInt();
		int[] score = new int[n];
		int[] minute = new int[n];

		for (int i = 0; i < n; i++) {
			score[i] = sc.nextInt();
		}
		for (int i = 0; i < n; i++) {
			minute[i] = sc.nextInt();
		}

		int Limak = 0;
		int l;
		int currmin = 0;
		for (int i = 0; i < n; i++) {
			currmin += minute[i];
			l = score[i] - (c * currmin);

			if (l > 0) {
				Limak += l;
			}
		}

		int Radewoosh = 0;
		int r;
		int curr = 0;

		for (int i = n - 1; i >= 0; i--) {
			curr += minute[i];
			r = score[i] - (c * curr);

			if (r > 0) {
				Radewoosh += r;
			}

		}

		if (Radewoosh == Limak) {
			System.out.println("Tie");
		} 
		else
			System.out.println((Radewoosh > Limak) ? "Radewoosh" : "Limak");

	}

}

