	String s[] = br.readLine().split(" ");
	int n = Integer.parseInt(s[0]);
	int k = Integer.parseInt(s[1]);
	char d[] = br.readLine().toCharArray();
	Arrays.sort(d);
	int ans = Integer.MAX_VALUE;
	for(int i = 0; i<n;i++){
	    int c = d[i]-'a'+1, p = 1;
	    char ul = d[i];
	    for(int j = i+1; j<n && p<k;j++){
		if(d[j]-ul>=2){
		    ul = d[j];
		    p++;
		    c+=d[j]-'a'+1;
		}
		if(p==k){
		    break;
		}
	    }
	    if(p==k){
		ans = Math.min(ans,c);
	    }
	}
	out.println(ans==Integer.MAX_VALUE?-1:ans);
	out.close();
    }
}

