#define M 1000000007
#define N 211111

#define maxN 555

int n, k;
int dp[maxN][maxN], dp2[maxN][maxN];
int w[maxN];

int add(int x, int y) {
	x += y;
	if (x >= M) x-= M;
	return (x);
}

