#define MAX 200010
#define MOD 1000000007

int n, m, h, sum[1010], dp[MAX][2];

void Pregenerate(){
    int i, j, l, res, x = 0;
    for (i = h; i >= 0; i--){
        sum[i] = x;
        x += i;
    }

    for (l = 0; l <= h; l++){
        for (i = n; i >= 0; i--){
            j = sum[l] - i;
            if (i > sum[l]) continue;
            if (j > m) continue;


            if (!l) res = 1;
            else{
                res = 0;
                if ((i + l) <= n) res = res + dp[i + l][(l - 1) & 1];
                if ((j + l) <= m) res = res + dp[i][(l - 1) & 1];
            }

            if (res >= MOD) res -= MOD;
            dp[i][l & 1] = res;
        }
    }
}

