#include<iostream>
using namespace std;
const int mod = 1e9 + 7;
const int N = 2e6 + 10;
long long fac[N], inv[N];
long long calInv(long long a, long long b)
{
	long long ret = 1LL;
	while (b)
	{
		if (b & 1) ret = ret * a % mod;
		a = a * a % mod;
		b >>= 1;
	}
	return ret;
}
int main()
{
	fac[0] = 1;
	for (int i = 1; i < N; ++i) 
	{
		fac[i] = fac[i - 1] * i % mod;
		inv[i] = calInv(fac[i], mod - 2);
	}
	long long n;
	cin >> n;
	n++;
	long long ans = 2LL * fac[2 * n - 1] % mod * inv[n] % mod * inv[n - 1] % mod + mod - 1; 
	ans %= mod;
	cout << ans << endl;
	return 0;
}

