const long long MAXN = 2000;
const long long mod = 1000000007;
long long C[MAXN][MAXN], nn[MAXN], f[MAXN];
long long a[MAXN];
long long n, k;
long long res = 0, s = 0;

long long next(long long a[], long long l, long long r, long long up)
{
	a[r + 1] = 0;
	a[l]++;
	for(long long i = l; i <= r; i++)
		if (a[i] > up)
		{
			a[i] = 1;
			a[i + 1]++;
		}
	if (a[r + 1])
		return 0;
	return 1;
}

long long pow(long long a, long long b)
{
	long long res = 1;
	for(long long i = 1; i <= b; i++)
		res = res * a % mod;
	return res;
}

int main()
{
	cin >> n >> k;
	for(long long i = 0; i <= n; i++)
		C[i][0] = 1;
	for(long long i = 1; i <= n; i++)
		for(long long j = 1; j <= n; j++)
			C[i][j] = (C[i - 1][j] + C[i - 1][j - 1]) % mod;
	nn[0] = 1;
	//for(long long i = 1; i <= n; i++, prlong longf("\n"))
	//	for(long long j = 1; j <= n; j++)
	//		prlong longf("%I64d ", C[i][j]);
	for(long long i = 1; i <= n; i++)
		nn[i] = nn[i - 1] * i % mod;
	res = pow(n - k, n - k);

	//prlong longf("%I64d\n", res);
	for(long long i = 1; i <= k; i++)
	{
		long long tmp = C[k - 1][i - 1] * nn[i - 1] % mod;
		long long left = k - i;
		for(long long j = i + 1; j <= k; j++)
			a[j] = 1;
		long long tmp2 = 0;
		while(1)
		{
			long long flag = 1;
			for(long long j = i + 1; j <= k; j++)
			{
				long long x = j, ok = 0;
				for(long long l = 1; l <= k; l++)
				{
					x = a[x];
					if (x <= i)
						ok = 1;
				}
				if (!ok)
				{
					flag = 0;
					break;
				}
			}
			tmp2 += flag;
			if (!next(a, i + 1, k, k))
				break;
		}
		s += tmp2 * tmp % mod;
		s %= mod;

	}
	cout << res * s % mod << endl;
	return 0;
}

