vi primes;
int bs(int lo, int hi, double key, int w){
    // printf("w = %d\n",w);
    while(hi >= lo){
        int mid  = (hi + lo)/2;
        // printf("%d %d %d, w/mid = %lf\n",lo,hi,mid,(double)w / mid);
        if(fabs((double)w / mid - key) <= eps) return mid;
        else if((double)w / mid > key) lo = mid + 1;
        else hi = mid - 1; 
    }
    return 1;
}
void test(double key){
    // printf("buscando %lf\n",key);
    for(int i=0; i<=1000000; ++i){
        int j = bs(1,1000000,key,i);
        if(fabs((double) i/j - key) <= eps){
            if(i == 0) j = 1;
            printf("%d/%d\n",i,j);
            return;
        }
    }
}
