using namespace std;
const int inf=2000+5;
int memo[inf][inf],m;
int pow_mod(int x,int y)
{
    if(y==0)
        return 1%m;

    if(y==1)
        return x%m;

    if(~memo[x][y])
        return memo[x][y];

    int ret=pow_mod(x,y/2) %m ;

    ret=( (ret%m) * (ret%m) )%m;

    return memo[x][y]=( pow_mod(x,y%2) *ret )%m;
}
int main()
{
    int x,y,temp,cnt=0;
    bool fg;
    cin>>m;
    memset(memo,-1,sizeof memo);
    for(x=1;x<m;x++)
    {
        fg=0;
        for(y=1;y<m;y++)
        {
            temp=(pow_mod(x,y)-1+m)%m;

            if(y==m-1){
                if(temp)
                    fg=1;
            }
            else if(!temp)
                fg=1;
        }
        if(!fg)
            cnt++;
    }
    printf("%d\n",cnt);
    return 0;
}

