#include <bits/stdc++.h>
using namespace std;

int MaxHr, MaxMin, ans;
vector<int> hr, minute;

int ok(vector<int> X)
{
    int mask = 0;

    for(int i=0; i<X.size(); i++)
    {
        if((1<<X[i])&mask)
            return 0;

        mask += (1<<X[i]);
    }

    int h = 0, m = 0;

    for(int i=0; i<hr.size(); i++)
        h = h*10 + X[i];
    for(int i=0; i<minute.size(); i++)
        m = m*10 + X[i+hr.size()];

    if(h>MaxHr || m>MaxMin)
        return 0;

    return 1;
}

void solve(vector<int> X, int num)
{
    if(num==hr.size() + minute.size())
    {
        if(ok(X))
            ans++;

        return;
    }

    for(int i=0; i<7; i++)
    {
        vector<int> Y = X;
        Y.push_back(i);
        solve(Y, num+1);
    }
}

int main()
{
    int N, M;
    cin >> N >> M;

    N--, M--;

    if(N==0)
        hr.push_back(0);
    if(M==0)
        minute.push_back(0);

    while(N)
    {
        hr.push_back(N%7);
        N/= 7;
    }

    while(M)
    {
        minute.push_back(M%7);
        M/= 7;
    }

    reverse(hr.begin(), hr.end());
    reverse(minute.begin(), minute.end());

    for(int i=0; i<hr.size(); i++)
        MaxHr = MaxHr*10 + hr[i];
    for(int i=0; i<minute.size(); i++)
        MaxMin = MaxMin*10 + minute[i];

    if(hr.size()+minute.size()>7)
        cout << 0 << endl;
    else
    {
        vector<int> X;
        solve(X, 0);
        cout << ans << endl;
    }

    return 0;
}

