using namespace std;
inline ll rd(){
	ll x=0;int ch=getchar(),f=1;
	while (!isdigit(ch)&&(ch!='-')&&(ch!=EOF)) ch=getchar();
	if (ch=='-'){f=-1;ch=getchar();}
	while (isdigit(ch)){x=(x<<1)+(x<<3)+ch-'0';ch=getchar();}
	return x*f;
}
inline void rt(ll x){
	if (x<0) putchar('-'),x=-x;
	if (x>=10) rt(x/10),putchar(x%10+'0');
		else putchar(x+'0');
}
const int maxn=55,p=1000000007,inv2=500000004;
int n;
ll f[maxn][maxn][maxn],g[maxn][maxn];
//f[i][j][k]��ʾi��1 j��2 ����ȥk���ߵķ�����
void upd(ll &x,ll y){
	x=(x+y)%p;
}
void prepare(){
	f[0][0][0]=1;
	rep(k,0,n-1) rep(j,0,n) rep(i,0,n-j){
		ll &v=f[i][j][k];
		if (j){
			if (i>1) upd(v,f[i-2][j-1][k]*(i*(i-1)/2));		//2-1 2-1
			if (i&&j>1) upd(v,f[i][j-2][k]*i*(j-1));		//2-1 2-2
			if (j>2) upd(v,f[i+2][j-3][k]*((j-1)*(j-2)/2));	//2-2 2-2
			if (i) upd(v,f[i-1][j-1][k-1]*i);				//2-1
			if (j>1) upd(v,f[i+1][j-2][k-1]*(j-1));			//2-2
			upd(v,f[i][j-1][k-2]*inv2);						//Nope
		}
		else if (i){
			if (i>1) upd(v,f[i-2][j][k]*(i-1));	//1-1
			upd(v,f[i-1][j][k-1]);				//Nope
		}
	}
	ll fac=1;
	rep(k,0,n-1){
		rep(i,0,n) rep(j,0,n-i) (f[i][j][k]*=fac)%=p;
		(fac*=k+1)%=p;
	}
}
int a[maxn];
int main(){
	n=rd();
	rep(i,1,n) a[i]=rd();
	prepare();
	g[1][a[1]]=1;
	ll v;
	rep(i,1,n-1) rep(j,1,n-i) if ((v=g[i][j])){
		int n1=0,n2=0;
		rep(k,i+1,i+j) if (a[k]==2) n1++;else n2++;
		rep(k,0,n1+2*n2) upd(g[i+j][k],v*f[n1][n2][k]);
	}
	rt(g[n][0]),putchar('\n');
}

