int f[1000010],op[100010],ans=1<<30;
bool b[1000010];

int main()
{
	for (int i=2;i<=1000000;i++)
	{
		if (b[i]==false) op[++op[0]]=i;
		for (int j=1;j<=op[0];j++)
		if (i*op[j]<=1000000) b[i*op[j]]=true;
		else break;
	}

	int x,y,o;
	scanf("%d",&x);
	for (int i=x-1;i>1;i--)
	if (x%i==0 && b[i]==false)
	{
		//printf("%d\n",i);
		y=(x/i-1)*i+1;
		o=0;
		for (int j=y;j<=x;j++)
		if (f[j]==0)
		{
			o++;
			if (o>500) break;
			f[j]=j;
			//if (pd(j)) continue;
			for (int k=j-1;k>1;k--)
			if (j%k==0 && b[k]==false)
			{
				f[j]=min(f[j],(j/k-1)*k+1);
				continue;
			}
			ans=min(ans,f[j]);
		}
		continue;
	}
	printf("%d\n",ans);
}

