		printf("%d\n", 10);
		return 0;
	}
	dp1[1] = 5;
	for(int i = 2; i <= n - 2; i++)
		dp1[i] = (3 + 2 * dp1[i - 1]) % MOD;
	long long dp2 = 1;
	FOR(i, n - 2)
		dp2 = (1 + dp1[n - 2 - i] * (3 + dp2)) % MOD;
