#define MD 1000000007

void su(int &a, int b)
{
	a+=b; if (a>=MD) a-=MD;
}

int bitcount(int x)
{
	int ret=0;
	while (x) x-=x&-x, ret++;
	return ret;
}

int tmask[7]={0,3,6,12,9,15,15};
int tcost[7]={0,1,1,1,1,2,2};
int C[10][10], g[16][13][16], all[16], cs[16][210][3], dp[210][210][16];

void lemon()
{
	int n,m; scanf("%d%d",&n,&m);
	C[0][0]=1;
	rep(i,1,5)
	{
		C[i][0]=C[i][i]=1;
		rep(j,1,i-1) C[i][j]=C[i-1][j-1]+C[i-1][j];
	}
	//dp[h][c][mask]
	//current height h, c triangles, covering section mask
	rep(mask,0,15)
		rep(ti,0,6)
			if ((mask&tmask[ti])==tmask[ti])
				rep(tk,0,15)
					if (((mask^tmask[ti])&tk)==tk)
					{
						int nmask=tmask[ti]|tk, cav=0;
						rep(i,0,3) if ((!(nmask&(1<<i))) && (!(nmask&(1<<((i+1)&3))))) cav|=(1<<i);
						rep(ci,0,15)
						{
							int cover=(ci|((ci&7)<<1)|(ci>>3));
							if ((cav&ci)==ci && (mask&cover)==cover)
							{
								int av[4], tv[4]; 
								rep(i,0,3) if (nmask&(1<<i)) av[i]=0; else av[i]=3;
								rep(i,0,3) if (ci&(1<<i)) av[i]--, av[(i+1)&3]--;
								rep(t0,0,av[0])
									rep(t1,0,av[1])
										rep(t2,0,av[2])
											rep(t3,0,av[3])
											{
												int cost=tcost[ti]+bitcount(tk)+bitcount(ci)+t0+t1+t2+t3; 
												tv[0]=t0; tv[1]=t1; tv[2]=t2; tv[3]=t3;
												int gmask=0; rep(i,0,3) if (tv[i]) gmask|=(1<<i);
												if ((mask&gmask)!=gmask) continue;
												int co=1;
												rep(i,0,3) co=co*C[av[i]][tv[i]];
												rep(mmask,0,15)
													if ((mmask&nmask)==0 && (mmask|nmask|gmask|cover)==mask)
														su(g[mask][cost][mmask],co);
											}
							}
						}
					}

	memset(all,0,sizeof all);
	rep(mask,0,15)
		rep(cost,0,12)
			rep(mmask,0,15)
				if (g[mask][cost][mmask])
				{
					all[mask]++;
					cs[mask][all[mask]][0]=cost;
					cs[mask][all[mask]][1]=mmask;
					cs[mask][all[mask]][2]=g[mask][cost][mmask];
				}

	dp[0][0][0]=1; 
	dp[0][1][1]=1; dp[0][1][2]=1; dp[0][1][4]=1; dp[0][1][8]=1;
	dp[0][1][3]=1; dp[0][1][6]=1; dp[0][1][12]=1; dp[0][1][9]=1;
	dp[0][2][3]=1; dp[0][2][5]=1; dp[0][2][9]=1; 
	dp[0][2][6]=1; dp[0][2][10]=1; dp[0][2][12]=1;
	dp[0][2][14]=2; dp[0][2][13]=2; dp[0][2][11]=2; dp[0][2][7]=2; dp[0][2][15]=2;
	dp[0][3][14]=1; dp[0][3][13]=1; dp[0][3][11]=1; dp[0][3][7]=1; dp[0][3][15]=4;
	dp[0][4][15]=1;
	rep(h,1,n)
		rep(c,0,m)
			rep(mask,0,15)
				rep(k,1,all[mask])
					if (c>=cs[mask][k][0])
						su(dp[h][c][mask],LL(dp[h-1][c-cs[mask][k][0]][cs[mask][k][1]])*cs[mask][k][2]%MD);

	int s=0;
	rep(mask,0,15) su(s,dp[n][m][mask]);
	rep(i,1,m) s=LL(s)*i%MD;
	printf("%d\n",s);
}

