int dx[9] = {0, -1, -1, -1, 0, 1, 1, 1, 0};
int dy[9] = {0, -1, 0, 1, 1, 1, 0, -1, -1};
int pos[3][2];
int king[2];

bool ok( int y, int x )
{
	int nx, ny;

	// atacado por torre
	fori(i,2) 
	{
		if ( pos[i][0] == y && pos[i][1] != x )
		{
			int minx = min( pos[i][1], x ) + 1;
			int maxx = max( pos[i][1], x );
			int j = minx;
			for( j = minx; j < maxx; ++j ) if ( y == pos[2][0] && j == pos[2][1] ) break;
			if ( j == maxx ) return false;
		}
		if ( pos[i][0] != y && pos[i][1] == x )
		{
			int miny = min( pos[i][0], y ) + 1;
			int maxy = max( pos[i][0], y );
			int j = miny;
			for( j = miny; j < maxy; ++j ) if ( x == pos[2][1] && j == pos[2][0] ) break;
			if ( j == maxy ) 
			{
				return false;
			}
		}
	}

	// atacado por rei
	forr(i,1,8)
	{
		ny = pos[2][0] + dy[i];
		nx = pos[2][1] + dx[i];

		if ( ny == y && nx == x )
		{
			return false; 
		}
	}

	return true;
}

bool can_move()
{
	int y, x;

	fori(i,9)
	{
		y = king[0] + dy[i];
		x = king[1] + dx[i];
		if ( VALID( x, y ) && ok( y, x ) ) return true;
	}

	return false;
}

