#include <bits/stdc++.h>

using namespace std;
typedef unsigned long long ull;
const int N_MAX = 50;
int N;
char str[N_MAX];
int cnt[N_MAX];

inline bool can00() {
  return cnt[0] >= 2;
}
inline bool can25() {
  return cnt[2] && cnt[5];
}
inline bool can50() {
  return cnt[5] && cnt[0];
}
inline bool can75() {
  return cnt[7] && cnt[5];
}

inline int calc(int l, int r) {
  int cnt = 0;
  char tmp[N_MAX];
  memcpy(tmp, str, sizeof(str));
  for (int i = N; i >= 1; i--) {
    if (tmp[i] == r) {
      while(i < N) swap(tmp[i], tmp[i+1]), cnt++, i++;
      break;
    }
  }
  for (int i = N-1; i >= 1; i--) {
    if (tmp[i] == l) {
      while(i < N-1) swap(tmp[i], tmp[i+1]), cnt++, i++;
      break;
    }
  }
  for (int i = 1; i <= N - 2; i++) {
    if (tmp[i] != 0) {
      while(i > 1) swap(tmp[i], tmp[i-1]), cnt++, i--;
      break;
    }
  }
  return cnt;
}
int main() {
  scanf("%s", str + 1);
  N = (int) strlen(str + 1);
  for (int i = 1; i <= N; i++) {
    cnt[str[i]-='0']++;
  }
  int ans = 0x3f3f3f3f;
  if (can00()) ans = min(ans, calc(0, 0));
  if (can25()) ans = min(ans, calc(2, 5));
  if (can50()) ans = min(ans, calc(5, 0));
  if (can75()) ans = min(ans, calc(7, 5));
  if (ans == 0x3f3f3f3f) ans = -1;
  printf("%d\n", ans);

}

