ll readInt ()
{
    bool minus = false;
    ll result = 0;
    char ch;
    ch = getchar();
    while (true)
    {
        if (ch == '-') break;
        if (ch >= '0' && ch <= '9') break;
        ch = getchar();
    }
    if (ch == '-') minus = true;
    else result = ch-'0';
    while (true)
    {
        ch = getchar();
        if (ch < '0' || ch > '9') break;
        result = result*10 + (ch - '0');
    }
    if (minus)
        return -result;
    else
        return result;
}
struct pt
{
    int x, y, id;
};
bool comp(pt a, pt b)
{
    return a.x < b.x || (a.x == b.x && a.y < b.y);
}
ll gcd(ll a, ll b)
{
    return b ? gcd(b, a % b) : a;
}
ll lcm(ll a, ll b)
{
    return a / gcd(a, b) * b;
}
ll binary_power(ll a, ll n)
{
    ll res = 1;
    while(n > 0)
    {
        if(n % 2 == 0)
        {
            a *= a;
            n >>= 1;
        }
        else
        {
            res *= a;
            n--;
        }
    }
    return res;
}
int bitCount(ll n)
{
    return __builtin_popcount(n);
}
int LowerBound(int L, int R, int x, ll w[])
{
    // L = 0, R = n
    while(R - L > 1)
    {
        int mid = (L + R) >> 1;
        if(w[mid] >= x)
            R = mid;
        else
            L = mid;
    }
    if(w[R] == x)
        return R;
    return -1;
}
int UpperBound(int L, int R, int x, ll w[])
{
    // L = 1, R = n + 1
    while(R - L > 1)
    {
        int mid = (L + R) >> 1;
        if(w[mid] <= x)
            L = mid;
        else
            R = mid;
    }
    if(w[L] == x)
        return L;
    return -1;
}
