int search(int v,int n,int k)
{
    int k_p=k;//存储的是题目中的k^p
    int total=v;
    while (v/k_p>=1)
    {
        total+=(int)v/k_p;
        k_p*=k;
    }
    if(total>n) return 1;
    if(total==n) return 0;
    return -1;
}
