long long x1, x2, x, a, b, res, sgn;

int gao() {
	if (x + a <= 0 && x + b >= 0) {
		res = x2;
		return 1;
	} else if (a + b >= 0) {
		return 0;
	} else if (b >= 0) {
		return 0;
	} else {
		a = -a;
		b = -b;
		long long r = x % (a + b);
		if (r == 0) {
			return -1;
		} else if (r >= b && r <= a) {
			res = x1 - r * sgn;
			return 1;
		} else {
			return 0;
		}
	}
}

