using namespace std;

int main()
{

    std::vector<int> avoid,avoid1;
    std::vector<int>::iterator it,it1,jt,jt1;
    int ans1=0,ans2=0;

    string pawn[8];
    for(int i=0;i<8;i++)
     {
        cin>>pawn[i];
     }

     for(int i=0;i<pawn[0].size();i++)
     {
         if(pawn[0][i]=='B')
         {
            it1=avoid.insert(it1,i);
            it1++;
         }
     }

    for(int i=0;i<pawn[7].size();i++)
     {
         if(pawn[7][i]=='W')
         {
            jt1=avoid1.insert(jt1,i);
            jt1++;
         }
     }


    for(int i=1;i<8;i++)
    {
        unsigned int j=0;
        while(j<pawn[i].size())
        {

            if(pawn[i][j]=='W')
            {
            //    cout<<"J: "<<j<<endl;
                it=find(avoid.begin(),avoid.end(),j);
                if(it==avoid.end())
                {
                    ans1=i;
                    break;
                }
            }
            else if(pawn[i][j]=='B')
            {
                it1=avoid.insert(it1,j);
                it1++;
            }
            j++;
        }
            if(ans1!=0)
                break;
    }


    for(int i=7;i>=0;i--)
    {
        unsigned int j=0;
        while(j<pawn[i].size())
        {
        //    cout<<"J: "<<j<<endl;
            if(pawn[i][j]=='B')
            {
                jt=find(avoid1.begin(),avoid1.end(),j);
                if(jt==avoid1.end())
                {
                    ans2=7-i;
                    break;
                }
            }
            else if(pawn[i][j]=='W')
            {
                jt1=avoid1.insert(jt1,j);
                jt1++;
            }
            j++;
        }
            if(ans2!=0)
                break;
    }

    if(ans1<=ans2)
    {
        cout<<"A";
    }
    else
        cout<<"B";
    return 0;
}

